function digiRain(e,t,n,r,i,s,o){var u=(new Date).getTime();var a=document.getElementById("DigiRain");a.style.color=i;a.style.visibility="hidden";var f="<table>";var l=-1;var c;while(++l<t){f+="<tr>";var h;c=-1;while(++c<e){h=String.fromCharCode(12448+Math.random()*96);f+='<td id="digiRain'+u+"-"+l+"-"+c+'" style="visibility: hidden">'+h+"</td>"}f+="</tr>"}f+="</table>";a.insertAdjacentHTML("beforeend",f);a.style.visibility="visible";var p=[];this.makeItRain=function(){var r=Math.random();if(r<n){var i=Math.floor(r*e);var s=new digiRainDroplet(0,i,t,u);p.push(s)}var o=[];var a;var f=-1;while(++f<p.length){a=p[f];if(!a.fall()){o.push(a)}}p=o};setInterval(this.makeItRain,r)}function digiRainDroplet(e,t,n,r){var i=10;var s=false;var o=.4+Math.random();var u=0;var a=.8;this.fall=function(){u+=o;if(u>a){u-=a;e++;if(e<n){document.getElementById("digiRain"+r+"-"+e+"-"+t).style.visibility="visible";var f=0;while(++f<i&&e>=f){document.getElementById("digiRain"+r+"-"+(e-f)+"-"+t).style.opacity=1-f/i}}if(e-i>=0){if(e-i>n){s=true}else{document.getElementById("digiRain"+r+"-"+(e-i)+"-"+t).style.visibility="hidden"}}}return s}}var fontSize=12;var fontSizeScale=fontSize+2;var width=window.screen.width/fontSizeScale*2;var height=window.screen.height/fontSizeScale;digiRain(width,height,.5,100,"#20F54A",fontSize,"#000000")